import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import { useState } from 'react';
import { useTodo } from './Todo';


function App() {
  const [state, dispatch]=useTodo();
    const {job,jobs}=state
    const inputRef=useRef()
    const handleSubmitJob=()=>{
        dispatch(addJob(job))
        dispatch(setJob(''))
        inputRef.current.focus()
    }
  return(
      <div className="post-list">
        <h3>Todo</h3>
          <input 
          ref={inputRef}
          value={job}
          placeholder="Enter todo..."
          onChange={e=>{
               dispatchJob(setJob(e.target.value))
          }}
          />
          <button className="m-2" onClick={job!=="" ? handleSubmitJob:undefined}>Add todo</button>
          <br />
          <ul>
              {jobs.map((job,index)=>(
                  <li key={index}>{job} <button className="btn-close" onClick={()=>dispatchJob(deleteJob(index))}></button></li>
              ))}
          </ul>
      </div>
  )
};

export default App;
