/**
 * @fileoverview This file contains the content component.
 * @author <NAME>
 */

import React, { useEffect, useReducer, useRef, useState, memo } from "react";

/**
 * This is a constant array that contains the table names.
 * @type {string[]}
 */
const table = ["posts", "comments", "albums", "photos", "todos", "users"];

/**
 * This is the content component.
 * @returns {JSX.Element} The content component.
 */
function Content() {
  const [title, setTitle] = useState("");
  const [posts, setPosts] = useState([]);
  const [type, setTypes] = useState("posts");
  const [showTop, setShowTop] = useState(false);
  const [width, setWidth] = useState(window.innerWidth);
  const [count, setCount] = useState(60);
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [producs, setProduct] = useState([]);
  // const [countdown, setCountdown] = useState(180);
  //useLayoutEffect => chạy trước tính toán tránh nhấp nháy giao diện
  //useCallBack sử dụng chung memo cho hàm khi đưa dữ liệu vào con mà con không sử dụng
  //useMemo tránh thực hiện lại logic không cẩn thiếts
  const [avatar, setAvatar] = useState();

  /**
   * This is an effect hook that is used to revoke the object URL when the component unmounts.
   */
  useEffect(() => {
    return () => {
      avatar && URL.revokeObjectURL(avatar.review);
    };
  }, [avatar]);

  /**
   * This is a function that is used to handle the avatar change event.
   * @param {React.ChangeEvent<HTMLInputElement>} e The change event.
   */
  const handleAvatar = (e) => {
    const file = e.target.files[0];
    file.review = URL.createObjectURL(file);
    setAvatar(file);
    //e.target.value=null;
  };

  /**
   * This is an effect hook that is used to fetch the posts when the type changes.
   */
  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/${type}`)
      .then((res) => res.json())
      .then((posts) => {
        setPosts(posts);
      });
  }, [type]);

  /**
   * This is an effect hook that is used to handle the scroll event.
   */
  useEffect(() => {
    const handleScroll = () => {
      setShowTop(window.scrollY >= 200);
    };
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  /**
   * This is an effect hook that is used to handle the resize event.
   */
  useEffect(() => {
    const handleResize = () => {
      setWidth(window.innerWidth);
    };
    window.addEventListener("resize", handleResize);
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  // useEffect(() => {
  //   const timerId = setInterval(() => {
  //     setCountdown((prevState) => prevState - 1);
  //   }, 1000);
  //   return () => clearInterval(timerId);
  // }, []);

  /**
   * This is a ref object that is used to access the name input element.
   */
  const nameRef = useRef();

  /**
   * This is a function that is used to handle the submit event.
   */
  const handleSubmit = () => {
    setProduct([...producs, { name, price: +price }]);
    setName("");
    setPrice("");
    nameRef.current.focus();
  };

  /**
   * This is a function that is used to calculate the total price.
   * @returns {number} The total price.
   */
  const totalPrice = useMemo(() => {
    const result = producs.reduce((result, prod) => {
      return result + prod.price;
    }, 0);
    return result;
  }, [producs]);

  return (
    <div>
      <input
        ref={nameRef}
        value={name}
        placeholder="Enter name..."
        onChange={(e) => setName(e.target.value)}
      />
      <input
        value={price}
        placeholder="Enter price..."
        onChange={(e) => setPrice(e.target.value)}
      />
      <button className="m-2" onClick={handleSubmit}>
        Add product
      </button>
      <br />
      Total: {totalPrice}
      <ul>
        {producs.map((product, index) => (
          <li key={index}>
            {product.name} - {product.price}
          </li>
        ))}
      </ul>
      <h1>{count}</h1>
      <button onClick={handleStart}>Start</button>
      <button onClick={handleStop}>Stop</button>
      <h1>Screen Width: {width}</h1>
      <input type="file" onChange={handleAvatar} />
      {avatar && (
        <img src={avatar.review} width="100" />
      )}
      <br />
      {/* <h1>Countdown: {countdown}</h1> */}
      {table.map((item) => (
        <button
          key={item}
          className={type === item ? "btn btn-primary" : "btn btn-outline-primary"}
          onClick={() => setTypes(item)}
        >
          {item}
        </button>
      ))}
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <ul>
        {posts.map((post) => (
          <li key={post.id}>{post.title || post.name}</li>
        ))}
      </ul>
      {showTop && (
        <button
          className="position-fixed bottom-0 end-0"
          onClick={() => window.scrollTo({ top: 0, behavior: "smooth" })}
        >
          Go to Top
        </button>
      )}
    </div>
  );
}

export default memo(Content);
