function App(){
    return (
        <div>
            <h3>Todo</h3>
            <input 
            ref={inputRef}
            value={job}
            placeholder="Enter todo..."
            onChange={e=>{
                dispatchJob(setJob(e.target.value))
            }}
            />
            <button className="m-2" onClick={job!=="" ? handleSubmitJob:undefined}>Add todo</button>
            <br />
            <ul>
                {jobs.map((job,index)=>(
                    <li key={index}>{job} <button className="btn-close" onClick={()=>dispatchJob(deleteJob(index))}></button></li>
                ))}
            </ul>
        </div>
    )
}

export default App()
