import { Routes, Route, Link } from 'react-router-dom'
import Home from '/pages'
import Contact from '/contact'
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import { useTodo, actions } from './Todo';
import { useEffect, useRef } from 'react';
import Video from './Watch'

function App() {
  const [state, dispatch]=useTodo();
  const {job,jobs}=state
  const inputRef=useRef()
  const handleSubmit=()=>{
      dispatch(actions.addJob(job))
      dispatch(actions.setJob(''))
      inputRef.current.focus()
  }
  const videoRef = useRef()
  useEffect(()=>{
    console.log(videoRef.current)
  })
  const handlePlayVideo=()=>{
    videoRef.current.play()
  }
  const handlePauseVideo=()=>{
    videoRef.current.pause()
  }
  const handleLoopVideo=()=>{
    videoRef.current.loop()
  }

  return(
      <div className="m-3">
        <h3>Todo</h3>
          <input 
          ref={inputRef}
          value={job}
          placeholder="Enter todo..."
          onChange={e=>{
               dispatch(actions.setJob(e.target.value))
          }}
          />
          <button className="m-2" onClick={job!=="" ? handleSubmit:undefined}>Add todo</button>
          <br />
          <ul>
              {jobs.map((job,index)=>(
                  <li key={index}>{job} <button className="btn-close" onClick={()=>dispatch(actions.deleteJob(index))}></button></li>
              ))}
          </ul>
          <br />
          <Video ref={videoRef} />
          <button className="m-2" onClick={handlePlayVideo}>Play video</button>
          <button className="m-2" onClick={handlePauseVideo}>Pause video</button>
          <button className="m-2" onClick={handleLoopVideo}>Loop video</button>
          <Link className='m-2 btn btn-outline-warning' to="/pages">Page</Link>
          <Link className='m-2 btn btn-outline-warning' to="/contact">Contact</Link>
          <Routes>
            <Route path='/pages' element={<Home />} />
            <Route path='/contact' element={<Contact />} />s
          </Routes>
      </div>
  )
};

export default App;
