import Tippy from '@tippyjs/react/headless';
import { Wrapper as PopperWrapper } from '~/components/Popper';
import AccountItem from '../Layout/components/AccountItem';
import { useState, useEffect } from 'react';
import classNames from 'classnames/bind';
import styles from './Search.module.scss';
import { ClearIcon, LoadingIcon, SearchIcon } from '../Icons';

const cx = classNames.bind(styles);

function Search() {
  const [searchResult, setSearchResult] = useState([]);
  useEffect(() => {
    setTimeout(() => {
      setSearchResult([1, 2]);
    }, 0);
  }, []);
  return (
    <div>
      <Tippy
        interactive
        delay={[0, 700]}
        render={(attrs) => (
          <div className={cx('search-result')} tabIndex="-1" {...attrs}>
            <PopperWrapper>
              <h4 className={cx('search-title')}>Accounts</h4>
              <div>
                <AccountItem />
                <AccountItem />
                <AccountItem />
              </div>
            </PopperWrapper>
          </div>
        )}
      >
        <div className={cx('search')}>
          <input placeholder="Search your fave..." spellCheck={false}></input>
          <ClearIcon className={cx('clear')} />
          <LoadingIcon className={cx('loading')} />
          <button className={cx('search-btn')}>
            <SearchIcon />
          </button>
        </div>
      </Tippy>
    </div>
  );
}

export default Search;
