import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import { useTodo, actions } from './Todo';
import { useRef } from 'react';
import Video from './Watch'

function App() {
  const [state, dispatch]=useTodo();
  const {job,jobs}=state
  const inputRef=useRef()
  const handleSubmit=()=>{
      dispatch(actions.addJob(job))
      dispatch(actions.setJob(''))
      inputRef.current.focus()
  }
  const videoRef =useRef()
  return(
      <div className="m-3">
        <h3>Todo</h3>
          <input 
          ref={inputRef}
          value={job}
          placeholder="Enter todo..."
          onChange={e=>{
               dispatch(actions.setJob(e.target.value))
          }}
          />
          <button className="m-2" onClick={job!=="" ? handleSubmit:undefined}>Add todo</button>
          <br />
          <ul>
              {jobs.map((job,index)=>(
                  <li key={index}>{job} <button className="btn-close" onClick={()=>dispatch(actions.deleteJob(index))}></button></li>
              ))}
          </ul>
          <br />
          <Video 
          ref={videoRef}
          />
          <button className="btn btn-primary" onClick={()=>videoRef.current.play()}>Play</button>
          <button className="btn btn-danger" onClick={()=>videoRef.current.pause()}>Pause</button>
      </div>
  )
};

export default App;
