import { SET_JOB, ADD_JOB, DELETE_JOB } from './constant'

const initStateJob = {
    job:'',
    jobs:[]
}

const reducerJob = (stateJob, actionJob)=>{
    console.log('Action:',actionJob)
    console.log('Previous state',stateJob)
    let newState
    switch(actionJob.type){
        case SET_JOB:
            newState = {
              ...stateJob,
                job:actionJob.payload
            }
            break;
        case ADD_JOB:
            newState = {
              ...stateJob,
                jobs:[...stateJob.jobs,actionJob.payload]
            }
            break;
        case DELETE_JOB:
            const newJobs=[...stateJob.jobs]
            newJobs.splice(actionJob.payload,1)
            newState = {
             ...stateJob,
                jobs:newJobs
            }
            break;
        default:
            throw new Error('Invalid action')
    }
    console.log('New State: ', newState)
    return newState
}
export default reducerJob
