import { useReducer, useRef } from "react"
import { setJob, addJob, deleteJob } from "./action"
import reducerJob, { initStateJob } from "./reducer"
import logger from "./logger"
export {default as TodoProvider} from "./Todo/Provider"
export {default as TodoContext} from "./Todo/Context"
export * from "./hooks"
function App(){
    const [stateJob, dispatchJob]=useReducer(logger(reducerJob),initStateJob)
    const {job,jobs}=stateJob
    const inputRef=useRef()
    const handleSubmitJob=()=>{
        dispatchJob(addJob(job))
        dispatchJob(setJob(''))
        inputRef.current.focus()
    }
    return (
        <div>
            <h3>Todo</h3>
            <input 
            ref={inputRef}
            value={job}
            placeholder="Enter todo..."
            onChange={e=>{
                dispatchJob(setJob(e.target.value))
            }}
            />
            <button className="m-2" onClick={job!=="" ? handleSubmitJob:undefined}>Add todo</button>
            <br />
            <ul>
                {jobs.map((job,index)=>(
                    <li key={index}>{job} <button className="btn-close" onClick={()=>dispatchJob(deleteJob(index))}></button></li>
                ))}
            </ul>
        </div>
    )
}

export default App
