import { useEffect, useReducer, useRef, useState, memo, useMemo } from "react";
const table=['posts','comments','albums','photos','todos','users'];
function Content(){
    const [title,setTitle]=useState('');
    const [posts,setPosts]=useState([]);
    const [type,setTypes]=useState('posts');
    const [showTop,setShowTop]=useState(false);
    const [width, setWidth]=useState(window.innerWidth);
    const [count, setCount]=useState(60);
    const [name, setName]=useState('')
    const [price, setPrice]=useState('')
    const initStateJob = {
        job:'',
        jobs:[]
    }
    const SET_JOB='set_job'
    const ADD_JOB='add_job'
    const DELETE_JOB='delete_job'
    const setJob= payload=>{
        return {
            type:SET_JOB,
            payload
        }
    }
    const addJob = payload=>{
        return {
            type:ADD_JOB,
            payload
        }
    }
    const deleteJob = payload=>{
        return {
            type:DELETE_JOB,
            payload
        }
    }
    const reducerJob = (stateJob, actionJob)=>{
        console.log('Action:',actionJob)
        console.log('Previous state',stateJob)
        let newState
        switch(actionJob.type){
            case SET_JOB:
                newState = {
                  ...stateJob,
                    job:actionJob.payload
                }
                break;
            case ADD_JOB:
                newState = {
                  ...stateJob,
                    jobs:[...stateJob.jobs,actionJob.payload]
                }
                break;
            case DELETE_JOB:
                const newJobs=[...stateJob.jobs]
                newJobs.splice(actionJob.payload,1)
                newState = {
                 ...stateJob,
                    jobs:newJobs
                }
                break;
            default:
                throw new Error('Invalid action')
        }
        console.log('New State: ', newState)
        return newState
    }
    const [producs, setProduct]=useState([])
    // const [countdown, setCountdown]=useState(180);
    //useLayoutEffect => chạy trước tính toán tránh nhấp nháy giao diện
    //useCallBack sử dụng chung memo cho hàm khi đưa dữ liệu vào con mà con không sử dụng
    //useMemo tránh thực hiện lại logic không cẩn thiếts
    const [avatar, setAvatar]=useState();
    useEffect(()=>{
        return ()=>{
            avatar && URL.revokeObjectURL(avatar.review)
        }
    },[avatar])
    const handleAvatar = (e) =>{
        const file = e.target.files[0]
        file.review=URL.createObjectURL(file)
        setAvatar(file)
        //e.target.value=null;
        
    }
    useEffect(()=>{
        fetch(`https://jsonplaceholder.typicode.com/${type}`)
        .then(res=>res.json())
        .then(posts=>{
            setPosts(posts);
        })
    },[type])
    
    useEffect(()=>{
        const handleScroll=()=>{
            setShowTop(window.scrollY>=200)
        }
        window.addEventListener('scroll',handleScroll)
    },[])
    useEffect(()=>{
        const handleResize=()=>{
            setWidth(window.innerWidth);
        }
        window.addEventListener('resize',handleResize);
        return ()=> {
            window.removeEventListener('resize',handleResize);
        }
    },[])
    // useEffect(()=>{
    //     const timerId = setInterval(()=>{
    //         setCountdown(prevState=>prevState-1)
    //     },1000)
    //     return () => clearInterval(timerId)
    // },[])
     
    let timerId = useRef()
    const handleStart= ()=>{
        timerId.current = setInterval(()=>{
            setCount(prevCount=>prevCount-1)
        },1000)
        console.log('Start',timerId.current);
    }
    const handleStop= ()=>{
        clearInterval(timerId.current)
        console.log('Stop',timerId.current)
    }
    const nameRef = useRef()
    const handleSubmit =()=>{
        setProduct([...producs,{
            name,
            price: +price
        }])
        setName('')
        setPrice('')
        nameRef.current.focus()
    }
    const totalPrice = useMemo(()=>{
        const result = producs.reduce((result,prod)=>{
            return result + prod.price
        },0)
        return result
    },[producs])
    const initState=0
    const UP_ACTION='up'
    const DOWN_ACTION='down'
    const reducer = (state,action)=>{
        switch(action){
            case UP_ACTION:
                return state+1
            case DOWN_ACTION:
                return state-1
            default:
                throw new Error(`Invalid action ${action}`)
        }
    }
    const [state, dispatch]=useReducer(reducer,initState)
    const [stateJob, dispatchJob]=useReducer(reducerJob,initStateJob)
    const {job,jobs}=stateJob
    const inputRef=useRef()
    const handleSubmitJob=()=>{
        dispatchJob(addJob(job))
        dispatchJob(setJob(''))
        inputRef.current.focus()
    }
    return (
        <div>
            <h3>Todo</h3>
            <input 
            ref={inputRef}
            value={job}
            placeholder="Enter todo..."
            onChange={e=>{
                dispatchJob(setJob(e.target.value))
            }}
            />
            <button className="m-2" onClick={job!=="" ? handleSubmitJob:undefined}>Add todo</button>
            <br />
            <ul>
                {jobs.map((job,index)=>(
                    <li key={index}>{job} <button className="btn-close" onClick={()=>dispatchJob(deleteJob(index))}></button></li>
                ))}
            </ul>
            <br />
            <h1>{state}</h1>
            <button className="m-2" onClick={()=>dispatch(UP_ACTION)}>Up</button>
            <button className="m-2" onClick={()=>dispatch(DOWN_ACTION)}>Down</button>
            <br />
            <input 
            ref={nameRef} 
            value={name}
            placeholder="Enter name..." 
            onChange={e=>setName(e.target.value)}
            />
            <input 
            value={price}
            placeholder="Enter price..." 
            onChange={e=>setPrice(e.target.value)}
            />
            <button className="m-2" onClick={handleSubmit}>Add product</button>
            <br />
            Total:{totalPrice}
            <ul>
                {producs.map((product,index)=>(
                    <li key={index}>{product.name} - {product.price}</li>
                ))}
            </ul>
            <h1>{count}</h1>
            <button onClick={handleStart}>Start</button>
            <button onClick={handleStop}>Stop</button>
            <h1>Screen Width: {width}</h1>
            <input type="file" onChange={handleAvatar} />
            {avatar && (<img src={avatar.review} width='100'></img>)}
            <br></br>
            {/* <h1>Countdown: {countdown}</h1> */}
            {table.map(item => <button key={item} className={type === item ?
            'btn btn-primary':
            'btn btn-outline-primary'}
            onClick={()=>setTypes(item)}
            >{item}</button>)}
            <input 
            value={title}
            onChange={e=>setTitle(e.target.value)}
            />
            <ul>
                {posts.map(post=>(
                    <li key={post.id}>{post.title || post.name}</li>
                ))}
            </ul>
            {showTop && 
            <button 
            className="position-fixed bottom-0 end-0"
            onClick={() => window.scrollTo({top: 0, behavior: 'smooth'})}
            >
                Go to Top
            </button>
            }
        </div>
    )
}
export default memo(Content);
