import Tippy from '@tippyjs/react/headless';
import { Wrapper as PopperWrapper } from '~/components/Popper';
import AccountItem from '../Layout/components/AccountItem';
import { useState, useEffect, useRef } from 'react';
import classNames from 'classnames/bind';
import styles from './Search.module.scss';
import { ClearIcon, LoadingIcon, SearchIcon } from '../Icons';

const cx = classNames.bind(styles);

function Search() {
  const [searchValue, setSearchValue] = useState('');
  const [searchResult, setSearchResult] = useState([]);
  const [showResults, setShowResults] = useState(true);

  const inputRef = useRef();

  useEffect(() => {
    fetch('https://tiktok.fullstack.edu.vn/api/users/search?q=hoaa&type=less')
      .then((res) => res.json())
      .then((res) => {
        setSearchResult(res.data);
      });
  }, [searchValue]);

  const handleClear = () => {
    setSearchValue('');
    setSearchResult([]);
    inputRef.current.focus();
  };
  const handleHideResult = () => setShowResults(false);

  return (
    <div>
      <Tippy
        interactive
        visible={showResults && searchResult.length > 0}
        delay={[0, 700]}
        render={(attrs) => (
          <div className={cx('search-result')} tabIndex="-1" {...attrs}>
            <PopperWrapper>
              <h4 className={cx('search-title')}>Accounts</h4>
              <div>
                {searchResult.map((result) => {
                  return <AccountItem key={result.id} data={result} />;
                })}
              </div>
            </PopperWrapper>
          </div>
        )}
        onClickOutside={handleHideResult}
      >
        <div className={cx('search')}>
          <input
            ref={inputRef}
            value={searchValue}
            placeholder="Search your fave..."
            spellCheck={false}
            onChange={(e) => setSearchValue(e.target.value)}
            onFocus={() => setShowResults(true)}
          ></input>
          {!!searchValue && (
            <button
              onClick={() => {
                handleClear();
              }}
            >
              <ClearIcon className={cx('clear')} />
            </button>
          )}
          {/* <LoadingIcon className={cx('loading')} /> */}
          <button className={cx('search-btn')}>
            <SearchIcon />
          </button>
        </div>
      </Tippy>
    </div>
  );
}

export default Search;
